import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
export class AuthService {
    constructor(http) {
        this.http = http;
        this._apiLink = environment.baseURL;
    }
    getUser() {
        return new Promise((resolve, reject) => {
            this.http.get(`${this._apiLink}/user`).subscribe({
                next: (res) => {
                    this._userList = res;
                    resolve();
                },
                error: (err) => {
                    reject(err);
                }
            });
        });
    }
    async checkAuthentication(currentUser) {
        await this.getUser();
        this._userList.map((item) => {
            if ((item.userName === currentUser.userName) && (item.password === parseInt(currentUser.password))) {
                this._isloggedin = true;
                localStorage.setItem('user', this._isloggedin);
            }
            else {
                this._isloggedin = false;
                localStorage.setItem('user', this._isloggedin);
            }
        });
        console.log(typeof (localStorage.getItem('user')));
    }
}
