        this._apiLink = environment.baseURL;
    }
    getUser() {
        return new Promise((resolve, reject) => {
            this.http.get(`${this._apiLink}/user`).subscribe({
                next: (res) => {
                    this._userList = res;
                    resolve();
                },
                error: (err) => {
                    reject(err);
                }
            });
        });
    }
    async checkAuthentication(currentUser) {
        await this.getUser();
        this._checkUser = await Promise.all(this._userList.map((item) => {
            if ((item.userName === currentUser.userName) && (item.password == currentUser.password)) {
                debugger;
                this._isloggedin = true;
                return true;
            }
            else {
                debugger;
                this._isloggedin = false;
                return false;
            }
        }));
        this.allowUser();
    }
    async allowUser() {
        await this._checkUser;
        localStorage.setItem('user', this._isloggedin);
        console.log('authService', localStorage.getItem('user'));
    }
}
